catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
  if (isLoading || newValue === '') {
    return;
  }

  // 确认用户上传了 Excel 文件
//   if (newValue.toLowerCase().endsWith('.xlsx') || newValue.toLowerCase().endsWith('.xls')) {
    g_form.addInfoMessage('正在处理 Excel 文件，请稍候...');

    // 获取当前请求项的 sys_id
    var requestItemId = g_form.getUniqueValue();

    // 调用服务器端脚本处理 Excel
    var ga = new GlideAjax('ExcelProcessor');
    ga.addParam('sysparm_name', 'processExcel');
    ga.addParam('sysparm_request_item', requestItemId);
    ga.addParam('sysparm_file_name', newValue);

    ga.onreadystatechange = function() {
      if (ga.readyState == 4 && ga.status == 200) {
        var response = JSON.parse(ga.responseXML.documentElement.getAttribute('answer'));

        if (response.success) {
          g_form.addInfoMessage('Excel 数据已成功导入到 MRVS！');
          g_form.refresh(); // 刷新表单以显示更新的 MRVS 数据
        } else {
          g_form.addErrorMessage('处理失败: ' + response.error);
        }
      }
    };
    ga.send();
//   } else {
//     g_form.addWarningMessage('请上传 .xlsx 或 .xls 格式的文件');
//   }
}
