sys_script_include.script:
var SubmitValueUtils = Class.create();
SubmitValueUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    submitPreviousValues: function() {
        var sysId = this.getParameter('sysparm_sysId');
        var question1 = this.getParameter('sysparm_squestion1');
        var question2 = this.getParameter('sysparm_squestion2');
        var question3 = this.getParameter('sysparm_squestion3');
        var gr = new GlideRecordSecure('sc_req_item');
        gr.addQuery('number', sysId);
        //var result ={};
        // gr.variables.Question1 = question1;
        // gr.variables.Question2 = question2;
        //gr.variables.Question3 = question3;
        var variables = {};
        if (gr.variables) {
            variables = JSON.parse(gr.variables);
        }
       gs.error('gr=============' + variables);
        // 添加或修改变量值
        variables['question1'] = question1; // 替换为你的变量名和值
        variables['question2'] = question2;
        variables['question3'] = question3;

        // 保存回记录
        gr.variables = JSON.stringify(variables);
        gr.state = 100;
        gr.update();
        gs.error('gr=============' + gr.state);
        return 'true';

    },

    type: 'SubmitValueUtils'
});
