sys_script_include.script:
var PDFGenerator = Class.create();
PDFGenerator.prototype = Object.extendsObject(AbstractAjaxProcessor, {
    generatePDF: function() {
        try {
            var tableName = this.getParameter('sysparm_table');
            var recordId = this.getParameter('sysparm_record_id');

            // 获取记录
            var gr = new GlideRecord(tableName);
            if (!gr.get(recordId)) {
                return "";
            }

            // 创建PDF内容
            var content = "=== 记录详情 ===\n\n";
            content += "编号: " + gr.getDisplayValue() + "\n";
            content += "创建日期: " + gr.sys_created_on + "\n\n";
            content += "描述:\n" + gr.description + "\n";

            // 生成PDF
            var renderer = new PDFRender();
            renderer.addContent(content);
            renderer.setHeader(gr.getRecordClassName() + " 详情");
            var pdf = renderer.render();

            // 保存为附件
            var attachment = new GlideSysAttachment();
            var fileName = gr.getDisplayValue() + ".pdf";
            var attachmentId = attachment.write(gr, fileName, 'application/pdf', pdf);

            return attachmentId;
        } catch (e) {
            gs.error("生成PDF失败: " + e);
            return "";
        }
    },
    type: 'PDFGenerator'
});
