sys_script_include.script:
var SubmitGenerator = Class.create();
SubmitGenerator.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    generate: function() {
        try {
            var sysId = this.getParameter('sysparm_sysId');
            var question1 = this.getParameter('sysparm_squestion1');
            var question2 = this.getParameter('sysparm_squestion2');
            var question3 = this.getParameter('sysparm_squestion3');
            var fromItem = this.getParameter('sysparm_from');
            var gr = new GlideRecord('sc_req_item');
            gr.addQuery('number', sysId);
            gr.query();

            if (gr.next()) {
                gr.variables.question1 = question1;
                gr.variables.question2 = question2;
                gr.variables.question3 = question3;
                gs.error('fromItem============' + fromItem);
                if (fromItem == 'rejected') {
                    gr.setValue('state', 2);
                } else if (fromItem == 'fulfiller') {
                    gr.setValue('state', 3);
                }
                gr.update();
                return 'true';
            }
        } catch (e) {
            return "";
        }
    },
    type: 'SubmitGenerator'
});
