var PDFGenerator = Class.create();
PDFGenerator.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    generatePDF: function() {
        try {
            var sysId = this.getParameter('sysparm_sysId');
            var question1 = this.getParameter('sysparm_squestion1');
            var question2 = this.getParameter('sysparm_squestion2');
            var question3 = this.getParameter('sysparm_squestion3');
            var gr = new GlideRecord('sc_req_item');
            gr.addQuery('number', sysId);
            gr.query();

            if (gr.next()) {
                // var variables = {};
                // if (gr.variables) {
                //     variables = JSON.parse(gr.variables);
                // }
                // gs.error('gr1=============' + variables);
                // // 添加或修改变量值
                // variables['question1'] = question1; // 替换为你的变量名和值
                // variables['question2'] = question2;
                // variables['question3'] = question3;
                gr.setValue('variables.question1', question1);
                // 保存回记录
                //gr.variables = JSON.stringify(variables);
                gs.error('gr2============' + gr.variables);
                gr.setValue('state', 2);
                gr.update();
                return 'true';
            }
        } catch (e) {
            gs.error("生成PDF失败: " + e);
            return "";
        }
    },
    type: 'PDFGenerator'
});
