sys_script_include.script:
var FieldUpdater = Class.create();
FieldUpdater.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    updateField: function() {
        var recordId = this.getParameter('sys_id');
        var fieldName = this.getParameter('sysparm_field_name');
        var newValue = this.getParameter('sysparm_new_value');
        // 更新记录
        var gr = new GlideRecord('x_1755713_new_em_0_employee_info');
        //gr.addQuery('sn_number', 'SN000001');
		gr.addQuery(fieldName, 'SN000001');
        gr.query();
        if (gr.next()) {
            //gr.fieldName = newValue;
            gr.setValue(fieldName, newValue);
            gr.update();
            return 'success';
        } else {
            return '记录未找到';
        }

    },

    type: 'FieldUpdater'
});
